import { Component, OnInit } from '@angular/core';
import { PortfolioService } from 'src/app/servicios/portfolio.service';
import { Chart } from 'chart.js/auto';


@Component({
  selector: 'app-hard-ysoft-skills',
  templateUrl: './hard-ysoft-skills.component.html',
  styleUrls: ['./hard-ysoft-skills.component.css']
})
export class HardYSoftSkillsComponent implements OnInit {

  hardSkills = [
    { name: 'Programación en Java', percent: 80 },
    { name: 'Diseño gráfico con Adobe Illustrator', percent: 70 },
    { name: 'Desarrollo de aplicaciones web con Angular', percent: 90 },
    { name: 'Bases de datos SQL', percent: 85 }
  ];

  softSkills = [
    { name: 'Trabajo en equipo', percent: 95 },
    { name: 'Comunicación efectiva', percent: 90 },
    { name: 'Liderazgo', percent: 80 },
    { name: 'Resolución de problemas', percent: 85 }
  ];

  constructor(private datosPortfolio: PortfolioService) { }

  ngOnInit(): void {
    this.datosPortfolio.obtenerDatos().subscribe(data => {
      console.log(data);
      this.createChart(data);
    });
  }

  createChart(data: any) {
    // Creamos el gráfico de Hard Skills
    const hardSkillsChart = new Chart('hard-skills-chart', {
      type: 'doughnut',
      data: {
        labels: this.hardSkills.map(skill => skill.name),
        datasets: [{
          data: this.hardSkills.map(skill => skill.percent),
          backgroundColor: ['#007bff', '#28a745', '#dc3545', '#ffc107']
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: true,
            position: 'bottom'
          }
        }
      }
    });

    // Creamos el gráfico de Soft Skills
    const softSkillsChart = new Chart('soft-skills-chart', {
      type: 'doughnut',
      data: {
        labels: this.softSkills.map(skill => skill.name),
        datasets: [{
          data: this.softSkills.map(skill => skill.percent),
          backgroundColor: ['#007bff', '#28a745', '#dc3545', '#ffc107']
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: true,
            position: 'bottom'
          }
        }
      }
    });
  }
}
