import { Component, OnInit } from '@angular/core';
import { PortfolioService } from 'src/app/servicios/portfolio.service';

@Component({
  selector: 'app-hard-ysoft-skills',
  templateUrl: './hard-ysoft-skills.component.html',
  styleUrls: ['./hard-ysoft-skills.component.css']
})
export class HardYSoftSkillsComponent implements OnInit {

  hardSkills = [
    { name: 'Programación en Java', percent: 80 },
    { name: 'Diseño gráfico con Adobe Illustrator', percent: 70 },
    { name: 'Desarrollo de aplicaciones web con Angular', percent: 90 },
    { name: 'Bases de datos SQL', percent: 85 }
  ];

  softSkills = [
    { name: 'Trabajo en equipo', percent: 95 },
    { name: 'Comunicación efectiva', percent: 90 },
    { name: 'Liderazgo', percent: 80 },
    { name: 'Resolución de problemas', percent: 85 }
  ];

  constructor(private datosPortfolio: PortfolioService) { }

  ngOnInit(): void {
    this.datosPortfolio.obtenerDatos().subscribe(data => {
      console.log(data);
    });
  }
}
