import { Component, OnInit } from '@angular/core';
import { EncabezadoService } from '../servicios/encabezado.service';

@Component({
  selector: 'app-encabezado',
  templateUrl: './encabezado.component.html',
  styleUrls: ['./encabezado.component.css']
})
export class EncabezadoComponent implements OnInit {

  loggedIn = false;
  editing = false;
  nombre = '';
  profesion = '';
  descripcion = '';

  constructor(private encabezadoService: EncabezadoService) { }

  ngOnInit(): void {
    this.nombre = this.encabezadoService.getNombre();
    this.profesion = this.encabezadoService.getProfesion();
    this.descripcion = this.encabezadoService.getDescripcion();
  }

  activarEdicion(): void {
    this.editing = true;
  }

  guardarEdicion(): void {
    this.editing = false;
    this.encabezadoService.setDescripcion(this.descripcion);
  }

}
